import axios from "axios";
import { useEffect, useState } from "react";
import "./dash.css";

export default function Dash({ book }) {

  const [user, SetUser] = useState({});

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`users/${book.publisherId}`);
      SetUser(res.data);
    };
    fetchUser();
  }, []);

  return (
    <div className="dash">
      <div className="row-md">
        <div className="book">
          <img src={user.bookCover} alt="" />
          <p className="sold">
            sold: {book.sold}{" "}
            <span
              className="date"
              style={{ textAlign: "right", float: "right", fontSize: "12px" }}
            >
              {book.date}
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}
