import "./withdraw.css";
import SideBar from "../../sidebar/SideBar";
import axios from "axios";
import { useState } from "react";
import { useAlert } from 'react-alert'

export default function Withdraw() {
    const [withdraw, setWithdraw] = useState("");
    const [error, setError] = useState("");

    const alert = useAlert()


    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(false);
        try {
          const res = await axios.post("/:id/withdraw", {
            withdraw,
          });
          res.data && window.location.replace("/withdraw");
        } catch (error) {
          setError(true);
        }
      };

  return (
    <div className="withdrawH">
      <SideBar />
      <div className="head">
        <div className="top-p">
          <p className="dashboard-p">
            Payment
            <i className="icon fas fa-bell">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span className="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>
      
      <div className="container">
        <h3 className="withdrawT">Withdraw</h3>
        <form className="row g-3" onSubmit={handleSubmit}>
          <div className="col-md-5">
            <label htmlFor="inputFirstName" className="form-label">
              Amount to withdraw
            </label>
            <input
              type="name"
              className="form-control"
              id="inputEmail4"
              placeholder="input amount to withdraw i.e: 2000"
              onChange={(e) => setWithdraw(e.target.value)}
            />
          </div>
            <button type="submit" className="withdrawButton btn-primary" onClick={() => {
        alert.show('Oh look, an alert!')
      }}>
              Withdraw
            </button>
        </form>
        {error && (
          <span style={{ color: "red", textAlign: "center" }}>
            Balance is insufficent to perform this operation
          </span>
        )}
      </div>
    </div>
  )
}
