import React, { useEffect, useState, useContext } from "react";
import Dash from "../dash/Dash";
import "./dashboard.css"
import SideBar from "../../sidebar/SideBar";
import axios from "axios";
import { Context } from "../../../context/Context";


export default function Dashboard() {

  const { user } = useContext(Context);

  const [upload, setUpload] = useState([]);

  useEffect(() => {
    const fetchUpload = async () => {
      const res = await axios.get("/upload/uploaded");
      setUpload(res.data);
    };
    fetchUpload();
  }, []);

  return (
    <div className="dashboard-g">
      <SideBar />
      <div className="dashboard-h">
        <div className="top">
          <p className="dashboard">
            Dashboard
            <i className="notification-icon bi bi-bell-fill">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span className="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>

      <div className="uploaded-books">
        <p>
          <span className="user">Welcome back {user.firstName}!</span> Your
          current account balance is $50. This page contain the list of books
          you've uploaded so far with purchased count
        </p>

        <Dash upload={upload} />
      </div>

      
    </div>
  );
}
