import "./dashbb.css";
import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";
const path = require("path");

export default function Dashbb() {

  const PF = "http://localhost:4000/images/"

  const { user } = useContext(Context);

  const [myUploads, setMyUploads] = useState([]);

  useEffect(() => {
    const fetchUploads = async () => {
      const res = await axios.get("/upload/my-uploads/" + user._id + path);
      console.log(res);
      setMyUploads(res.data);
    };
    fetchUploads();
  }, [path]);

  return (
    <div className="dashbb-g">
      <div className="row-md">
        <div className="book">
          <ul>
            <>
            {myUploads.map((myUpload) => (
              <li key={myUpload.id} style={{ fontSize: "2em" }}>
                {myUpload.bookCover && (
                  <img src={PF + myUpload.bookCover} alt="" />
                )}
                
              {/* {myUpload.bookTitle} */}
              <p className="purchase-count" style={{ fontSize: "0.5em" }}>
                Sold: 20
              </p>
            </li>
            ))} 
            </>           
          </ul>

          {/* {myUploads.map((myUpload) => (
            <div>
              {myUpload.photo && (
                <img key={myUpload.id} src={myUpload.bookCover} alt="" />
              )}
            </div>
          ))}
          <p className="purchase-count" style={{ fontSize: "1em" }}>
            Sold: 20
          </p> */}
        </div>
      </div>
    </div>
  );
}
