import "./withdraw.css";
import SideBar from "../../sidebar/SideBar";
import axios from "axios";
import { useContext, useState } from "react";
import { Context } from "../../../context/Context";
import { Alert } from "react-bootstrap";

export default function Withdraw() {
  const [withdraw, setWithdraw] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const { user, dispatch } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    dispatch({ type: "UPDATE_START" });
    try {
      const res = await axios.put("publishers/withdraw/" + user._id, {
        publisherId: user._id,
        withdraw,
      });
      setSuccess(true);
      dispatch({ type: "UPDATE_SUCCESS", payload: res.data });
      // window.location.replace("/payment");
    } catch (error) {
      dispatch({ type: "UPDATE_FAILURE" });
      setError(true);
    }
  };

  if (success){

  return (
    <div className="withdrawH">
      <Alert variant="danger" onClose={() => setSuccess(false)} dismissible>
        <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
        <p>
        Withdrawal for input amount successful! Money will be deposited into
            your account within next 24hrs.
        </p>
      </Alert>
      <SideBar />
      <div className="head">
        <div className="top-p">
          <p className="dashboard-p">
            Payment
            <i className="icon fas fa-bell">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span className="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>

      <div className="containerW">
        <h3 className="withdrawT">Withdraw</h3>
        <form className="row g-3" onSubmit={handleSubmit}>
          <div className="col-md-5">
            <label htmlFor="inputFirstName" className="form-label">
              Amount to withdraw
            </label>
            <input
              type="name"
              className="form-control"
              id="inputEmail4"
              placeholder="input amount to withdraw i.e: 2000"
              onChange={(e) => setWithdraw(e.target.value)}
            />
          </div>
          <button type="submit" className="withdrawButton btn-primary" onClick={() => setSuccess(true)}>
            Withdraw
          </button>
        </form>
        {error && (
          <span
            style={{ color: "red", textAlign: "center", marginTop: "35px" }}
          >
            Balance is not sufficent to perform this operation
          </span>
        )}
        {/* {success && (
          <span
            className="profileSuccess"
            style={{ color: "green", textAlign: "center" }}
          >
            Withdrawal for input amount successful! Money will be deposited into
            your account within next 24hrs.
          </span>
        )} */}
      </div>
    </div>
  );
}
}

render(<Withdraw />)
