import React, { useState } from "react";
import showPwdImg from "./assets/showpd.svg";
import hidePwdImg from "./assets/hidepd.svg";
import PhoneInput from "react-phone-number-input";
import "./register.css";
import logo from "./assets/lg.png";
import axios from "axios";

export default function Register() {
  const [pwd, setPwd] = useState("");
  const [isRevealPwd, setIsRevealPwd] = useState(false);
  const [value, setValue] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [password,setPassword] = useState("");
  const [currency, setCurrency] = useState("");
  // const [confirmPassword,setConfirmPassword] = useState("");
  const [error,setError] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false)
    try {
      const res = await axios.post("/_auth/_register", {
      firstName, lastName, email, phoneNumber, password,
    });
    res.data && window.location.replace("/login")
    } catch (error) {
      setError(true);
    }
    
    
  };
  return (
    <div className="register-h">
      <div className="container-md">
        <div className="logo">
          <img className="lib-lg" src={logo} alt="" />
          <span className="txt-lg">
            <b>eLibrary</b>
          </span>
        </div>
      </div>

      <div className="container">
        <p className="register">
          <b>register</b>
        </p>
        <form className="row g-3" onSubmit={handleSubmit}>
          <div class="col-md-5">
            <label for="inputFirstName" class="form-label">
              First Name
            </label>
            <input type="name" class="form-control" id="inputEmail4" 
            onChange={e=>setFirstName(e.target.value)}
            />
          </div>
          <div class="col-md-5">
            <label for="inputLastName" class="form-label">
              Last Name
            </label>
            <input type="name" class="form-control" id="inputEmail4" onChange={e=>setLastName(e.target.value)} />
          </div>
          <div class="col-md-5">
            <label for="inputEmail4" class="form-label">
              Email
            </label>
            <input type="email" class="form-control" id="inputEmail4" onChange={e=>setEmail(e.target.value)} />
          </div>
          <div className="col-md-5">
            <label for="inputFirstName" class="form-label">
              Phone Number
            </label>
            <PhoneInput
              class="form-control"
              international
              defaultCountry="NG"
              value={value}
              onChange={setValue}
              value={phoneNumber}
              onChange={setPhoneNumber}
              // onChange={e=>setPhoneNumber(e.target.value)}
            />
          </div>
          <div className="col-md-5">
          <label for="validationCustom04" class="form-label">
            Currency
          </label>
          <select className="form-select" id="validationCustom04" required onChange={e=>setCurrency(e.target.value)}>
            <option selected disabled value="">
              Choose...
            </option>
            <option>AUD</option>
            <option>CAD</option>
            <option>GBP</option>
            <option>GHC</option>
            <option>JPY</option>
            <option>NGN</option>
            <option>USD</option>
          </select>
          <div className="invalid-feedback">Please select a valid Currency.</div>
        </div>
          <div className="col-md-5">
            <label for="inputPassword4" className="form-label">
              Password
            </label>
            <input
              name="pwd"
              type={isRevealPwd ? "text" : "password"}
              className="form-control"
              id="inputPassword4"
              // value={pwd}
              // onChange={(e) => setPwd(e.target.value)}
              onChange={e=>setPassword(e.target.value)}
            />
            <img
              className="sp"
              title={isRevealPwd ? "Hide password" : "Show password"}
              src={isRevealPwd ? hidePwdImg : showPwdImg}
              onClick={() => setIsRevealPwd((prevState) => !prevState)}
            />
            {/* <i className="eye far fa-eye"></i> */}
          </div>

          <div class="row-12">
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                id="gridCheck"
                required
              />
              <label class="form-check-label" for="gridCheck">
                You accept to our terms of service by checking this box
              </label>
            </div>
          </div>
          <div class="col-12">
            <button type="submit" class="btnn btn-primary">
              Sign in
            </button>
            <div className="pub">Already a publisher! <span className="member-l"><a href="/login">Login</a></span></div>
          </div>
        </form>
        {error && <span style={{color: "red", textAlign:"center"}}>Email already exit!</span>}
      </div>
    </div>
  );
}
