import "./dashbb.css";
import React, { useContext } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";


export default class Dashbb extends React.Component {
  state = {
    loading: true,
    people: [],
  };

  async componentDidMount(   { user } = useContext(Context)
  ) {
    const url = axios.get("/publishers/my-uploads/"+user._id);
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ people: data.results, loading: false });
  }

  render() {
    if (this.state.loading) {
      return <div>loading</div>;
    }
    if (!this.state.people.length) {
      return <div>didn't get a person</div>;
    }

    return (
      <div className="dashbb-g">
        <div className="row-md">
          <div className="book">
            {this.state.people.map((person) => (
              <div key={person.login.uuid}>
                <div>{person.name.title}</div>
                <div>{person.name.first}</div>
                <div>{person.name.last}</div>
                <img src={person.picture.large}/>
              </div>
            ))}
            {/* <ul>
            <li style={{ fontSize: "2em" }}>
              Hello
              <p className="purchase-count" style={{ fontSize: "0.5em" }}>
                Sold: 20
              </p>
            </li>
          </ul> */}
          </div>
        </div>
      </div>
    );
  }
}
