import "./upload.css";
import SideBar from "../../sidebar/SideBar";
import { useContext, useState } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";

export default function Upload() {
  const [uploadBook, setUploadBook] = useState("");
  const [bookCover, setBookCover] = useState("");
  const [bookTitle, setBookTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [bookDescription, setBookDescription] = useState("");
  const [currency, setCurrency] = useState("");
  const [price, setPrice] = useState("");
  const [publisherId, setPublisherId] = useState("");
  const [email, setEmail] = useState("");
  const { user } = useContext(Context);
  const [error,setError] = useState(false);


  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   const newUpload = {
  //     email: user.firstName,
  //     bookTitle,
  //     bookDescription,
  //     author,
  //     currency,
  //     price,
  //     publisherId,
  //     email,
  //   };
  //   if (uploadBook && bookCover) {
  //     const data = new FormData();
  //     const uploadBookName = Date.now() + uploadBook.name;
  //     const bookCoverName = Date.now() + bookCover.name;
  //     data.append("name", uploadBookName && bookCoverName);
  //     data.append("file", uploadBook && bookCover);
  //     newUpload.photo = bookCoverName && uploadBook;
  //     try {
  //       await axios.post("/update", data);
  //     } catch (err) {}
  //     try {
  //       const res = await axios.post("/upload", {
  //         newUpload
  //       });
  //       window.location.reload("/upload" + res.data._id)
  //     } catch (error) {}
  //   }
    
  // };
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false)
    try {
      const res = await axios.post("/upload", {
      uploadBook, bookCover, bookTitle, bookDescription, author, publisherId, price, currency, email
    });
    res.data && window.location.reload("/upload")
    } catch (error) {
      setError(true);
    }
    
    
  };

  return (
    <div className="upload">
      <SideBar />
      <div className="head-u">
        <div className="top-u">
          <p className="upload-u">
            Upload
            <i className="not-icon bi bi-bell-fill">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span class="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>

      <form className="upload-doc" onSubmit={handleSubmit}>
        <div class="mb-3">
          <label htmlFor="fileInput">Upload Book in PDF</label>
          <input
            type="file"
            class="form-control"
            aria-label="file example"
            accept="application/pdf"
            required
            onChange={e=>setUploadBook(e.target.value)}
          />
        </div>
        <br />
        <div class="mb-3">
          <label htmlFor="fileInput">
            Upload Book Cover design in jpeg, jpg or png
          </label>
          <input
            type="file"
            id="fileInput-1"
            class="form-control"
            aria-label="file example"
            accept="image/jpeg, image/jpg, image/png"
            required
            onChange={e=>setBookCover(e.target.value)}
          />
        </div>

        <div className="upload-form-group">
          <label for="upload-input">Book Title:</label>
          <input
            type="text"
            placeholder="Book Title"
            className="upload-input"
            autoFocus={true}
            required
            onChange={e=>setBookTitle(e.target.value)}
          />
        </div>

        <div className="upload-form-group">
          <label for="upload-input">Author:</label>
          <input
            type="text"
            placeholder="Author Name"
            className="upload-input"
            required
            onChange={e=>setAuthor(e.target.value)}
          />
        </div>

        <div className="upload-form-group mb-3">
          <label for="upload-input">Description:</label>
          <textarea
            placeholder="Add Book description"
            type="text"
            className="upload-input"
            required
            onChange={e=>setBookDescription(e.target.value)}
          ></textarea>
        </div>

        <div class="col-md-3">
          <label for="validationCustom04" class="form-label">
            Currency
          </label>
          <select class="form-select" id="validationCustom04" required onChange={e=>setCurrency(e.target.value)}>
            <option selected disabled value="">
              Choose...
            </option>
            <option>AUD</option>
            <option>CAD</option>
            <option>GBP</option>
            <option>GHC</option>
            <option>JPY</option>
            <option>NGN</option>
            <option>USD</option>
          </select>
          <div class="invalid-feedback">Please select a valid Currency.</div>
        </div>
        <div className="upload-form-group">
          <label for="upload-input">Price:</label>
          <input
            type="number"
            placeholder="Book Price"
            className="upload-input"
            required
            onChange={e=>setPrice(e.target.value)}
          />
        </div>
        <div className="upload-form-group">
          <label for="upload-input">Publisher Id:</label>
          <input
            type="text"
            placeholder="Publisher Id"
            className="upload-input"
            required
            onChange={e=>setPublisherId(e.target.value)}
          />
        </div>
        <div className="upload-form-group">
          <label for="upload-input">Email:</label>
          <input
            type="text"
            placeholder="Publisher Email"
            className="upload-input"
            required
            onChange={e=>setEmail(e.target.value)}
          />
        </div>

        <div class="col-12">
          <div class="form-check">
            <input
              class="form-check-input"
              type="checkbox"
              value=""
              id="invalidCheck"
              required
            />
            <label class="form-check-label" for="invalidCheck">
              Agree to terms and conditions
            </label>
          </div>
        </div>
        <button class="submit-button" type="submit">
          Upload
        </button>
      </form>
    </div>
  );
}
