import React, { useState } from "react";
import { CountryDropdown, RegionDropdown, CountryRegionData } from 'react-country-region-selector';
import showPwdImg from "./assets/showpd.svg";
import hidePwdImg from "./assets/hidepd.svg";
import PhoneInput from "react-phone-number-input";
import "./edit-profile.css";
import SideBar from "../../sidebar/SideBar";


export default function EditProfile() { 
  const [pwd, setPwd] = useState("");
  const [isRevealPwd, setIsRevealPwd] = useState(false);
  const [value, setValue] = useState();
  return (
    <div className="profile-edit-h">
      <SideBar />
      <div className="head-e">
        <div className="top-e">
          <p className="dashboard-e">
            Edit Profile
            <i class="icon fas fa-bell">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span class="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>

      <div className="cover-pic-e">
        <img
          src="https://cdn.psychologytoday.com/sites/default/files/styles/article-inline-half-caption/public/field_blog_entry_images/2018-09/shutterstock_648907024.jpg?itok=0hb44OrI"
          alt=""
        />
        <label htmlFor="fileInput"><span className="edit"><i class="fas fa-pen"></i></span></label>
        <input type="file" id="fileInput" style={{display:"none"}} />
      </div>

      <div className="profile-wrapper">
      <form className="row g-3">
          <div className="col-md-4">
              <label for="inputFirstName" class="form-label">Fist Name</label>
              <input class="form-control" type="text" placeholder="Jane" />
          </div>
          <div className="col-md-4">
              <label for="inputFirstName" class="form-label">Last Name</label>
              <input class="form-control" type="text" placeholder="Adams" />
          </div>
          <div className="col-md-4">
              <label for="inputFirstName" class="form-label">Address</label>
              <input class="form-control" type="text" placeholder="1, Kingdom street" />
          </div>
          <div className="col-md-4">
              <label for="inputFirstName" class="form-label">Phone Number</label>
              <PhoneInput class="form-control"
              international
              defaultCountry="NG"
              value={value}
              onChange={setValue}
            />
          </div>
          <div className="col-md-4">
              <label for="inputFirstName" class="form-label">Password</label>
              <input class="form-control"
              name="pwd"
              type={isRevealPwd ? "text" : "password"}
              class="form-control"
              id="inputPassword4"
              value={pwd}
              onChange={(e) => setPwd(e.target.value)}
            />
            <img
              className="e-sp"
              title={isRevealPwd ? "Hide password" : "Show password"}
              src={isRevealPwd ? hidePwdImg : showPwdImg}
              onClick={() => setIsRevealPwd((prevState) => !prevState)}
            />
            </div>
              <button className="edit-profile-button">Update</button>
          </form>
      </div>
    </div>
  );
}

class Example extends Component {
    constructor (props) {
      super(props);
      this.state = { country: '', region: '' };
    }
  
    selectCountry (val) {
      this.setState({ country: val });
    }
  
    selectRegion (val) {
      this.setState({ region: val });
    }
  
    render () {
      const { country, region } = this.state;
      return (
        <div>
          <CountryDropdown
            value={country}
            onChange={(val) => this.selectCountry(val)} />
          <RegionDropdown
            country={country}
            value={region}
            onChange={(val) => this.selectRegion(val)} />
        </div>
      );
    }
  }
