import "./dashbb.css";
import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";

export default function Dashbb() {
  const { user } = useContext(Context);

  const [myUploads, setMyUploads] = useState([]);

  useEffect(() => {
    const fetchUploads = async () => {
      const res = await axios.get("/upload/my-uploads/" + user._id);
      console.log(res);
      setMyUploads(res.data);
    };
    fetchUploads();
  }, []);

  return (
    <div className="dashbb-g">
      <div className="row-md">
        <div className="book">
          {/* <ul>
            <>
            {myUploads.map((myUpload) => (
              <li key={myUpload.id} style={{ fontSize: "2em" }}>
              {myUpload.bookTitle}
              <p className="purchase-count" style={{ fontSize: "0.5em" }}>
                Sold: 20
              </p>
            </li>
            ))} 
            </>           
          </ul> */}
          {myUploads.map((myUpload) => (
            <div>
              {myUpload.photo && (
                <img key={myUpload.id} src={myUpload.bookCover} alt="" />
              )}
            </div>
          ))}
          <p className="purchase-count" style={{ fontSize: "1em" }}>
            Sold: 20
          </p>
        </div>
      </div>
    </div>
  );
}
