import React, { useEffect, useState, useContext } from "react";
import Dash from "../dash/Dash";
import "./dashboard.css";
import SideBar from "../../sidebar/SideBar";
import { Books } from "../../../dummyData"

import { Context } from "../../../context/Context";

export default function Dashboard() {
  const { user } = useContext(Context);

  // const [myUploads, setmyUploads] = useState([]);

  // useEffect(() => {
  //   const fetchUpload = async () => {
  //     const res = await axios.get("/publishers/my-uploads/"+user._id);
  //     setmyUploads(res.data);
  //   };
  //   fetchUpload();
  // }, []);

  return (
    <div className="dashboard-g">
      <SideBar />
      <div className="dashboard-h">
        <div className="top">
          <p className="dashboard">
            Dashboard
            <i className="notification-icon bi bi-bell-fill">
              <span className="position-absolute top-50 start-100 translate-middle p-1 bg-danger border border-light rounded-circle">
                <span className="visually-hidden">New alerts</span>
              </span>
            </i>
          </p>
        </div>
      </div>

      <div className="welc">
        <p>
          <span className="user">Welcome back {user.firstName}!</span> Your
          current account balance is {user.currency}50. This page contain the
          list of books you've uploaded so far with purchased count
        </p>
      </div>
      <div className="books">
        {Books.map(b => (
          <Dash key={b.id} book={b} />
        ))}
      </div>
    </div>
  );
}
