import React, { useState } from "react";
import showPwdImg from "./assets/showpd.svg";
import hidePwdImg from "./assets/hidepd.svg";
import "./login.css";
import logo from "./assets/lg.png";
import { Context } from "../../../context/Context";
import axios from "axios";
import { useContext, useRef } from "react";

export default function Login() {
  const userRef = useRef();
  const passwordRef = useRef();
  const { user, dispatch, isFetching } = useContext(Context);

  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch({ type: "LOGIN_START" });
    try {
      const res = await axios.post("_auth/login", {
        email: userRef.current.value,
        password: passwordRef.current.value,
      });
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
    } catch (err) {
      dispatch({ type: "LOGIN_FAILURE" });
    }
  };

  console.log(user);

  const [pwd, setPwd] = useState("");
  const [isRevealPwd, setIsRevealPwd] = useState(false);
  return (
    <div className="head-l">
      <div className="container-md">
        <div className="logo">
          <img className="lib-lg" src={logo} alt="" />
          <span className="txt-lg">
            <b>eLibrary</b>
          </span>
        </div>
      </div>

      <div className="container">
        <p className="login">
          <b>Login</b>
        </p>
        <form class="row g-3" onSubmit={handleSubmit}>
          <div class="row-md-6">
            <label for="inputEmail4" class="form-label">
              Email
            </label>
            <input
              type="email"
              class="form-control"
              id="inputEmail4"
              ref={userRef}
            />
          </div>
          <div class="row-md-6">
            <label for="inputPassword4" class="form-label">
              Password
            </label>
            <input
              name="pwd"
              type={isRevealPwd ? "text" : "password"}
              class="form-control"
              id="inputPassword4"
              // value={pwd}
              // onChange={(e) => setPwd(e.target.value)}
              ref={passwordRef}
            />
            <img
              className="l-sp"
              title={isRevealPwd ? "Hide password" : "Show password"}
              src={isRevealPwd ? hidePwdImg : showPwdImg}
              onClick={() => setIsRevealPwd((prevState) => !prevState)}
            />
            {/* <i className="eye far fa-eye"></i> */}
          </div>

          <div class="col-12">
            <button type="submit" class="btn-n btn-primary">
              Login in
            </button>
            <div className="member">
              Not a publisher?{" "}
              <span className="member-r">
                <a href="/register">Register</a>
              </span>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
