import "./dashbb.css";
import React from "react";

export default class Dashbb extends React.Component {
  state = {
    loading: true,
    people: [],
  };

  async componentDidMount() {
    const url = "/publishers/my-uploads/"+user._id;
    const response = await fetch(url);
    const data = await response.json();
    this.setState({ people: data.results, loading: false });
  }

  render() {
    if (this.state.loading) {
      return <div>loading</div>;
    }
    if (!this.state.people.length) {
      return <div>didn't get a person</div>;
    }

    return (
      <div className="dashbb-g">
        <div className="row-md">
          <div className="book">
            {this.state.people.map((person) => (
              <div key={person.publisherId}>
                <div>{person.myUploads.bookTitle}</div>
                {/* <div>{person.myUploads.first}</div> */}
                {/* <div>{person.myUploads.last}</div> */}
                <img src={person.myUploads.coverPicture}/>
              </div>
            ))}
            {/* <ul>
            <li style={{ fontSize: "2em" }}>
              Hello
              <p className="purchase-count" style={{ fontSize: "0.5em" }}>
                Sold: 20
              </p>
            </li>
          </ul> */}
          </div>
        </div>
      </div>
    );
  }
}
