import "./dashbb.css";
import React, { useContext, useEffect, useState } from "react";
import axios from "axios";
import { Context } from "../../../context/Context";

export default function Dashbb() {

  const { user } = useContext(Context);

  const [myUploads, setMyUploads] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      const res = await axios.get("/publishers/my-uploads/"+user._id);
      console.log(res)
      setMyUploads(res.data);
    };
    fetchPosts();
  }, []);

  return (
    <div className="dashbb-g">
      <div className="row-md">
        <div className="book">
          <ul>
            {this.state.myUploads.map(myUpload => (
              <li key={myUpload.id} style={{ fontSize: "2em" }}>
              {myUpload.bookTitle}
              <p className="purchase-count" style={{ fontSize: "0.5em" }}>
                Sold: 20
              </p>
            </li>
            ))}            
          </ul>
        </div>
      </div>
    </div>
  );
}
